// Pong ... tm(Q) when(Q) elapsed(Q) remote(Q) local(I) world(I)
func (sess *Session) Pong(reader *packet.Reader) {
	tm := reader.Long()
	when := reader.Long()
	local := reader.Int()

	w := packet.CreateProxyWriter(19)
	w.Long(tm)
	w.Long(when)
	w.Long(123)                              // elapsed
	w.Long(uint64(time.Now().Unix()) * 1000) // remote
	w.UInt(uint32(local))
	w.UInt(uint32(time.Now().Unix())) // world
	w.Send(sess.conn)
}

// ChangeState ... State(I)
func (sess *Session) ChangeState(state uint32) {
	w := packet.CreateProxyWriter(0)
	w.UInt(state)
	w.Send(sess.conn)
}

// FinishState ... State(I)
func (sess *Session) FinishState(reader *packet.Reader) {
	state := reader.Int()
	fmt.Println("[PROXY] State: ", state)

	switch state {
	case 0:
		sess.ChangeState(1)
		sess.State1()
	case 1:
		sess.ChangeState(2)
		//sess.State2()
	case 2:
		sess.ChangeState(3)
		//sess.State3()
	case 3:
		sess.ChangeState(4)
		//sess.State4()
	case 4:
		sess.ChangeState(5)
		//sess.State5()
	case 5:
		sess.ChangeState(6)
		//sess.State6()
	case 6:
		sess.ChangeState(7)
		sess.State7()
	default:
		fmt.Println("[PROXY, err] No such state:", state)
	}
}

// SetGameType ... State(I)
func (sess *Session) SetGameType() {
	name := "e_rainbow_field_3"

	w := packet.CreateProxyWriter(15)
	w.String(name)
	w.Long(0) // checksum
	w.Byte(1) // immersive
	w.Send(sess.conn)
}

